import { useForm } from "react-hook-form";
import axios from "axios";
import { Link, useNavigate } from "react-router-dom";
import { useState } from "react";
import { send } from "emailjs-com";
import { useContext } from "react";
import { AuthContext } from "../context/AuthContext";
import Registro from "./Registro";
import ModificarPass from "./ModificarPass";
import "../pages/Home.css";

const LogIn = () => {
  const [passwordError, setPasswordError] = useState("");
  const { gestionarLogIn } = useContext(AuthContext);
  const [showLogin, setShowLogin] = useState(true);
  const [verCambiar, setVerCambiar] = useState(true);
  const [darkmode, setDarkmode] = useState(
    localStorage.getItem("darkMode") === "true"
  );

  const cambiarpass = () => {
    setVerCambiar(false);
  };
  // console.log(verCambiar);

  const toggleForm = () => {
    setShowLogin(!showLogin);
  };

  const gestorFormulario = async (data) => {
    try {
      const response = await axios.post(
        process.env.REACT_APP_API_URL + "/api/usuarios/login",
        {
          email: data.email,
          password: data.password,
        }
      );
      // console.log("Todo correcto", response.data);
      localStorage.setItem("datosUsuario", JSON.stringify(response.data));
      gestionarLogIn(
        response.data.token,
        response.data.userId,
        response.data.nombre
      );
      navigate("/home");
    } catch (error) {
      // console.log("algo fall√≥");
      if (error.response.status === 500) {
        setPasswordError(
          <>
            Usuario o contrase√±a incorrecta. Ha olvidado su contrase√±a? Pulse{" "}
            <span className="reset-link">
              <Link href="" onClick={cambiarpass}>
                aqui
              </Link>
            </span>{" "}
            para reestablecerla.
          </>
        );
      }
    }
  };

  const navigate = useNavigate();
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm();

  return (
    <div className={darkmode ? "principal-Dark" : "principal"}>
      <div className={darkmode ? "present-Dark" : "present"}>
        <h1 className="titulo">Encuentra tus cosas en segundos con Where Is</h1>
        <h2 className="subtitulo">
          Crea tu propia base de datos de tu casa y organiza tus cosas como
          nunca antes
        </h2>
        <br />
        <p className={darkmode ? "descripcion-Dark" : "descripcion"}>
          ¬øAlguna vez has perdido algo en tu casa y no sabes por d√≥nde empezar a
          buscar? Where is es la soluci√≥n que estabas buscando. Con nuestra
          aplicaci√≥n, podr√°s crear tu propia base de datos de tu casa y
          organizar todas tus cosas de manera f√°cil y sencilla. ¬øC√≥mo funciona?
          Primero, crea tu perfil y agrega tus habitaciones, armarios y cajones.
          Luego, agrega tus cosas a cada uno de ellos. Podr√°s agregar detalles
          como descripci√≥n, foto, fecha de adquisici√≥n y m√°s. Una vez que hayas
          agregado tus cosas, podr√°s buscarlas f√°cilmente en nuestra aplicaci√≥n.
          Simplemente ingresa el nombre de lo que est√°s buscando y te
          mostraremos d√≥nde est√°. ¬°No m√°s perder tiempo buscando cosas perdidas!
          Pero eso no es todo, nuestra aplicaci√≥n tambi√©n te permite crear
          listas de cosas que necesitas comprar, llevar un registro de tus cosas
          prestadas y mucho m√°s. ¬°Descubre lo f√°cil que es organizar tu casa con
          Where is ! Reg√≠strate ahora y comienza a disfrutar de una vida m√°s
          organizada.
        </p>
      </div>
      <div className="formulariosLoginSingUp">
        {showLogin ? (
          <div className="login">
            <h2>Log in</h2>
            <br />
            <form
              className="formulario"
              action=""
              onSubmit={handleSubmit(gestorFormulario)}
            >
              <input
                type="text"
                name="email"
                placeholder="Email"
                className="password"
                {...register("email", {
                  pattern:
                    /^(?![_.-])((?![_.-][_.-])[a-zA-Z\d_.-]){0,63}[a-zA-Z\d]@((?!-)((?!--)[a-zA-Z\d-]){0,63}[a-zA-Z\d]\.){1,2}([a-zA-Z]{2,14}\.)?[a-zA-Z]{2,14}$/,
                  required: true,
                })}
              />
              {errors.email && errors.email.type === "required" && (
                <p>Campo email requerido</p>
              )}
              {errors.email && errors.email.type === "pattern" && (
                <p>Formato de email incorrecto</p>
              )}
              <input
                type="password"
                name="password"
                placeholder="Password"
                className="password"
                {...register("password", {
                  minLength: 5,
                  required: true,
                })}
              />
              {errors.password && errors.password.type === "required" && (
                <p>Campo requerido</p>
              )}
              {errors.password && errors.password.type === "minLength" && (
                <p>Debe tener al menos 5 caracteres </p>
              )}
              {passwordError && <p className="error">{passwordError}</p>}
              <br />
              <div className="botonesLogIn">
                <button className="EnviarLogin" type="submit">
                  Enviar
                </button>
                <br />
                <br />
                <button onClick={toggleForm}>Sign up</button>
              </div>
            </form>
          </div>
        ) : (
          <div className="formulario">
            <Registro />
            <br />
            <br />
            <button onClick={toggleForm}>Log in</button>
          </div>
        )}
      </div>

      {verCambiar === false && (
        // <div className="verCambiar">
        <ModificarPass />
        // </div>
      )}
    </div>
  );
};

export default LogIn;
